mapper.insert:
INSERT INTO
NEWSLETTER(UNUM, NAME, ID, STATEMENT) VALUES(#{uNum}, #{name}, #{id},
'Y');

mapper.select:
SELECT PNUM as pNum,
IMG_PATH as img, TITLE as pTitle, TIME as time, DIFFICULTY as
difficulty FROM POSE;

SELECT COUNT(LNUM)
FROM
LIKEY WHERE UNUM = #{uNum};

SELECT COUNT(FNUM)
FROM
FEED WHERE UNUM = #{uNum};
